/** App.js */
import React from "react";

// Components
import MultilineChart from "./views/MultilineChart";
import TimeChart from "./views/TimeChart";

// Data 
import schc from "./SCHC.json";
import vcit from "./VCIT.json";
import portfolio from "./portfolio.json";
import attendance from "./attendance.json";

// Styling
import "./styles.css";

// Set the name, color, and data for portfolio data for the graph
const portfolioData = {
  name: "Portfolio",
  color: "#ffffff",
  items: portfolio.map((d) => ({ ...d, date: new Date(d.date) }))
};

// Set the name, color, and data for schc data for the graph
const schcData = {
  name: "SCHC",
  color: "#d53e4f",
  items: schc.map((d) => ({ ...d, date: new Date(d.date) }))
};

// Set the name, color, and data for the vcit data for the graph
const vcitData = {
  name: "VCIT",
  color: "#5e4fa2",
  items: vcit.map((d) => ({ ...d, date: new Date(d.date) }))
};

// Set the name, color, and dat for the attendace data for the graph
const attendanceData = {
  name: "Attendance",
  color: "#fa2b3c",
  items: attendance.map((d) => ({ ...d }))
}

// Set the dimensions of the graph
const dimensions = {
  width: 600,
  height: 300,
  margin: {
    top: 30,
    right: 30,
    bottom: 30,
    left: 60
  }
};

export default function App() {
  return (
    <div className="App">
        <div className="Column">
        <h1>Instructions:</h1>
        <ol>
          <li>Upload the time Availability form </li>
          <li>Fill out your time availability</li>
          <li>Checkout the graph</li>
        </ol>
        <h1> MultilineChart: </h1>
        <div className="Graph">
          {/* Add a chart component with the desired data and dimensions */}
          <MultilineChart
            data={[portfolioData, schcData, vcitData]}
            dimensions={dimensions}
          />
        </div>

        <h1> Time Availability Chart: </h1>
        <div className="Graph">
          <TimeChart
            data={[portfolioData, schcData, vcitData]}
            dimensions={dimensions}
          />
        </div>
      </div>
    </div>
  );
}
