import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { selectAll, svg } from 'd3';

const Heatmap = () => {

    // Create a reference for our svg
    const Chart = useRef();

    // Set the dimensions of the chart svg
    const dimensions = {
        width: 900,
        height: 600,
        margin: {top:130, left:50, bottom:70, right:50}
    }

    const rectSize = 30;

    // Sample data
    const data = [
        {day: 'Sun', dayValue: 0},
        {day: 'Mon', dayValue: 1},
        {day: 'Tue', dayValue: 2},
        {day: 'Wed', dayValue: 3},
        {day: 'Thu', dayValue: 4},
        {day: 'Fri', dayValue: 5},
        {day: 'Sat', dayValue: 6}
    ]

    useEffect( () => {
        // Define the size and posision of our svg
        const svg = d3.select(Chart.current)
                        .attr('width', dimensions.width)
                        .attr('height', dimensions.height)
                        .style('background-color', 'black')
                        .style('border', '1px solid white')

        // Create the x-scale
        const x = d3.scaleLinear()
                    .domain([0, data.length])
                    .range([0, dimensions.width])

        // Create the y-scale

        // Add the labels for the days
        svg.append('g')
            .selectAll('text')
            .data(data)
            .join('text')
            .text( d => `${d.day}`)
            .attr('x', (d) => x(d.dayValue) + dimensions.margin.left) 
            .attr('y', dimensions.height - dimensions.margin.bottom)
            .attr('fill', 'white')
            .style('font-size', 14)

        // Draw blocks
        data.forEach((day, i) => {
            // AM
            svg.append('g')
                .selectAll('rect')
                .data(data)
                .join('rect')
                .attr('x', x(day.dayValue) + dimensions.margin.left)
                .attr('y', (d,j) => j*(rectSize + 2) + dimensions.margin.top)
                .attr('width', rectSize)
                .attr('height', rectSize)
                .attr('fill', 'red')
        })

    });



    return (
        <div>
            <h1> Heatmap </h1>
            <svg ref={Chart}/>
        </div>
    );
}

export default Heatmap;
