/** App.js */
import React, { useState } from "react";

// Components
import Heatmap from "./views/Heatmap";

// Data 
import attendance from "./attendance.json";

// Styling
import "./styles.css";


// Set the name, color, and dat for the attendace data for the graph
const attendanceData = {
  name: "Attendance",
  color: "#fa2b3c",
  items: attendance.map((d) => ({ ...d }))
}

// Set the dimensions of the graph
const dimensions = {
  width: 600,
  height: 300,
  margin: {
    top: 30,
    right: 30,
    bottom: 30,
    left: 60
  }
};

export default function App() {  
  // Some hooks
  const [graphColor, setGraphColor] = useState("red");

  const [csvFile, setCsvFile] = useState();
  return (
    <div className="App">
      <h1>Time Availability Chart</h1>
      <div style={{display: 'flex', flexDirection: 'row'}}>
        <div>
          {console.log(graphColor)}
          <Heatmap graphColor={graphColor}/>
          <p>**Note: Left side of each day is AM and right side is PM**</p>
        </div>
          <div>
            <h2>Instructions:</h2>
            <ol>
              <li>Make a copy of the <a href="https://docs.google.com/forms/d/1Q9GmCV1sBuWhfXHPwB5gfENiLMtLGXoYc9uOl0DU2Sw/edit?usp=sharing" color = "red">Google Form</a> and have members of your group fill it out:</li>

              <li>Export the file from google forms as a csv file once all the responses are collected.</li>
              <li>Upload the same csv file to this page.</li>
              <li>Make customizations to the graph.</li>
              <li>View the graph.</li>
            </ol>

            <h2>Upload a CSV File:</h2>
            <form>
              <input type={"file"} accept={".csv"}/>
              <h1></h1>
              <button style={{background:"deepskyblue", border:"blue", color:"white", width:100, height:30}}>Upload</button>
            </form>

            <div>
              <h2>Customizations:</h2>
                <div>
                  <p>Select desired graph color: </p>
                  <select onChange={(e) => setGraphColor(e.target.value)}>
                    <option value="red">red</option>
                    <option value="orange">orange</option>
                    <option value="yellow">yellow</option>
                    <option value="lime">lime</option>
                    <option value="green">green</option>
                    <option value="blue">blue</option>
                    <option value="purple">purple</option>
                    <option value="magenta">magenta</option>
                  </select>
                </div>

                <div>
                  <p>Set the weight of each response (0-100)</p>
                  <input type={"text"} placeholder="Never" style={{width:40}}/>
                  <input type={"text"} placeholder="Once or Twice" style={{width:40}}/>
                  <input type={"text"} placeholder="Often" style={{width:40}}/>
                  <input type={"text"} placeholder="Almost Always" style={{width:40}}/>
                  <input type={"text"} placeholder="Always" style={{width:40}}/>
                </div>
            </div>

          </div>
      </div>      
    </div>
  );
}
