/** App.js */
import React from "react";

// Components
import MultilineChart from "./views/MultilineChart";
import TimeChart from "./views/TimeChart";
import Heatmap from "./views/Heatmap";

// Data 
import schc from "./SCHC.json";
import vcit from "./VCIT.json";
import portfolio from "./portfolio.json";
import attendance from "./attendance.json";

// Styling
import "./styles.css";

// Set the name, color, and data for portfolio data for the graph
const portfolioData = {
  name: "Portfolio",
  color: "#ffffff",
  items: portfolio.map((d) => ({ ...d, date: new Date(d.date) }))
};

// Set the name, color, and data for schc data for the graph
const schcData = {
  name: "SCHC",
  color: "#d53e4f",
  items: schc.map((d) => ({ ...d, date: new Date(d.date) }))
};

// Set the name, color, and data for the vcit data for the graph
const vcitData = {
  name: "VCIT",
  color: "#5e4fa2",
  items: vcit.map((d) => ({ ...d, date: new Date(d.date) }))
};

// Set the name, color, and dat for the attendace data for the graph
const attendanceData = {
  name: "Attendance",
  color: "#fa2b3c",
  items: attendance.map((d) => ({ ...d }))
}

// Set the dimensions of the graph
const dimensions = {
  width: 600,
  height: 300,
  margin: {
    top: 30,
    right: 30,
    bottom: 30,
    left: 60
  }
};

export default function App() {
  return (
    <div className="App">
      <h1>Instructions:</h1>
      <ol>
        <li>Make a copy of the <a href="https://docs.google.com/forms/d/1Q9GmCV1sBuWhfXHPwB5gfENiLMtLGXoYc9uOl0DU2Sw/edit?usp=sharing" color = "red">Google Form</a> and have members of your group fill it out:</li>

        <li>Export the file from google forms as a csv file once all the responses are collected.</li>
        <li>Upload the same csv file to this page.</li>
        <li>View the graph</li>
      </ol>
      <h1> MultilineChart: </h1>
      <div className="Graph">
        {/* Add a chart component with the desired data and dimensions */}
        <MultilineChart
          data={[portfolioData, schcData, vcitData]}
          dimensions={dimensions}
        />
      </div>

      <h1> Time Availability Chart: </h1>
      <div className="Graph">
        <TimeChart
          data={[portfolioData, schcData, vcitData]}
          dimensions={dimensions}
        />
      </div>

      <div>
        <h1>Upload a CSV File:</h1>
        <form>
          <input type={"file"} accept={".csv"}/>
          <h1></h1>
          <button style={{background:"salmon"}}>Import File</button>
        </form>

        <h1>{"\n"}</h1>
      </div>

      <div>
        <Heatmap/>
      </div>
    </div>
  );
}
