import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { selectAll } from 'd3';

const Heatmap = () => {

    const Chart = useRef();

    const dimensions = {
        width: 800,
        height: 600,
        margin: {top:130, left:50, bottom:70, right:50}
    }

    const data = [
        {day: 'Sunday', dayValue: 0},
        {day: 'Monday', dayValue: 1},
        {day: 'Tuesday', dayValue: 2},
        {day: 'Wednesday', dayValue: 3},
        {day: 'Thursday', dayValue: 4},
        {day: 'Friday', dayValue: 5},
        {day: 'Saturday', dayValue: 6}
    ]

    useEffect( () => {
        // define the size and posision of our svg
        const svg = d3.select(Chart.current)
                        .attr('width', dimensions.width)
                        .attr('height', dimensions.height)
                        .style('background-color', 'black')
                        .style('border', '1px solid white')

        const x = d3.scaleLinear()
                    .domain([0, data.length])
                    .range([0, dimensions.width])

        svg.append('g')
            .selectAll('text')
            .data(data)
            .join('text')
            .text( d => `${d.day}`)
            .attr('x', (d,i) => x(d.dayValue) + dimensions.margin.left) 
            .attr('y', dimensions.height - dimensions.margin.bottom)
            .attr('fill', 'white')
            .style('font-size', 5)
    });

    return (
        <div>
            <h1> Heatmap </h1>
            <svg ref={Chart}/>
        </div>
    );
}

export default Heatmap;
